import React, { useState } from 'react';
import "./Navbar.css";
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import {Link} from 'react-router-dom';


const Navbar = () => {
  const [menu, setMenu] = useState("shop")
  return (
    <div className='navbar'>
      <div className="nav-logo">
        <img src={logo} alt="logo" />
        <p>Shopper</p>
      </div>
      <ul className='nav-menu'>
        <li onClick={()=>{setMenu("shop")}}> <Link to=''>Shop {menu==="shop"?<hr/>:<></>}</Link> </li>
        <li onClick={()=>{setMenu("men")}}><Link to=''>Men {menu==="men"?<hr/>:<></>}</Link></li>
        <li onClick={()=>{setMenu("women")}}><Link to=''>Women {menu==="women"?<hr/>:<></>}</Link> </li>
        <li onClick={()=>{setMenu("kids")}}><Link to=''>Kids {menu==="kids"?<hr/>:<></>}</Link> </li>
      </ul>
      <div className="nav-login-cart">
        <button>Login</button>
        <img src={cart_icon} alt="cart-icon" />
        <div className="nav-cart-count">100</div>
      </div>
    </div>

  )
}

export default Navbar;
